import React from 'react';
import './collection-item.styles.scss';
import { connect } from "react-redux";
import { addItem } from "../../redux/cart/cart.actions";
// import CustomButton from "../custom-button/custom-button.component";
import { CollectionFooter, ImageContainer, PriceSpan, NameSpan, CollectionCustomButton, CollectionItemsContainer } from "./collection-item.styles";

const CollectionItem = ({ item, addItem }) => {
    const { price, name, imageUrl } = item;
    return (
        <CollectionItemsContainer>
            <ImageContainer
            style={
                {
                    backgroundImage : `url(${imageUrl})`
                }
            }/>
            <CollectionFooter>
                <NameSpan className="name">{name}</NameSpan>
                <PriceSpan className="price">{price}</PriceSpan>
            </CollectionFooter>
            <CollectionCustomButton onClick={() => {addItem(item)}} inverted> Add to cart</CollectionCustomButton>
        </CollectionItemsContainer>
    )
}

const mapDispatchToProps = dispatch => ({
    addItem: item => dispatch(addItem(item))
})

export default connect(null, mapDispatchToProps)(CollectionItem);
