import  CartActionTypes  from "./cart.types";

const INITIAL_STATE = {
    hidden: true
}

const cartReducer = () => {
    switch (action.type) {
        case TOGGLE_CART_HIDDEN:
            return {
                ...state,
                hidden: !state.hidden
            }
            default:
                return state;
    }
}

export default cartReducer;
